/* Matugen Theme */

@import "../colors.css";

* {
  font-family: "JetBrainsMono Nerd Font";
  font-weight: bold;
  min-height: 0;
  /* set font-size to 100% if font scaling is set to 1.00 using nwg-look */
  font-size: 97%;
  font-feature-settings: '"zero", "ss01", "ss02", "ss03", "ss04", "ss05", "cv31"';
  /* padding: 0.03125rem; */
}

#workspaces button:hover {
  border-radius: 10px;
  color: var(--surface_container_highest);
  background-color: var(--surface);
  padding-left: 2px;

#battery.critical:not(.charging) {
  background-color: var(--error);
  color: var(--on_background);
  animation-name: blink;

border: 0px solid var(--primary);
  padding-top: 1px;
  padding-bottom: 1px;
  padding-right: 4px;

.notification {
  color: @on_surface;
  background: alpha(@accent, .55);
  padding: 7px 7px 7px 0px;
  border-radius: 10px;
  border: 1px solid @surface_container;
}

.notification-default-action {
  margin: 0;
  padding: 0;
  font-size: 0.1rem;
  border-radius: 10px;
  background: @surface_container;
}
.notification-default-action:hover,
.notification-action:hover {
  color: @on_surface;
  background: @selected;
}

.inline-reply-entry {
  background: @surface_container;
  color: @on_surface;
  caret-color: @on_surface;
  border: 1px solid @surface_container;
  border-radius: 10px;
}

.inline-reply-button {
  font-size: 0.5rem;
  margin-left: 4px;
  background: @surface_container;
  border: 1px solid @surface_container;
  border-radius: 10px;
  color: @on_surface;
}

.inline-reply-button:disabled {
  background: initial;
  color: @on_surface;
  border: 1px solid transparent;
}

.inline-reply-button:hover {
  background: @surface_container_high;
}

.summary {
  font-size: 0.9rem;
  /* font-weight: bold; */
  background: transparent;
  color: @on_surface;
  text-shadow: none;
}

.time {
  font-size: 0.8rem;
  /* font-weight: bold; */
  background: transparent;
  color: @on_surface_variant;
  text-shadow: none;
  margin-right: 18px;
}

.body {
  font-size: 0.8rem;
  /* font-weight: bold; */
  background: transparent;
  color: @on_surface;
  text-shadow: none;
}

.widget-title {
  color: @on_surface;
  background: @surface_container;
  padding: 3px 6px;
  margin: 5px;
  font-size: 1rem;
  border-radius: 10px;
}

.widget-title > button {
  font-size: 0.75rem;
  color: @on_surface;
  border-radius: 10px;
  background: transparent;
  border: 0.5px solid @surface_container;
}

/* clear button */
.widget-title > button:hover {
  background: @on_surface;
  color: @error;
}

.widget-dnd {
  background: @surface_container;
  padding: 3px 6px;
  margin: 5px;
  border-radius: 12px;
  font-size: 0.5rem;
  color: @surface_container;
}

.widget-dnd > switch {
  border-radius: 12px;
  /* border: 1px solid @accent; */
  background: @on_surface;
}

.widget-dnd > switch:checked {
  background: @error;
}

.widget-dnd > switch slider {
  background: @surface_container;
  border-radius: 12px;
}

.widget-dnd > switch:checked slider {
  background: @surface_container;
  border-radius: 12px;
}

.widget-buttons-grid {
  font-size: large;
  color: @surface_container;
  padding: 2px;
  margin: 5px;
  border-radius: 10px;
  background: @surface_container;
}

.widget-buttons-grid > flowbox > flowboxchild > button {
  margin: 1px;
  background: @surface_container;
  border-radius: 10px;
  color: @on_surface;
}

/* individual buttons */
.widget-buttons-grid > flowbox > flowboxchild > button:hover {
  background: @on_surface;
  color: @surface_container;
}

