  color: @on_primary_container;
  background: @primary_container;
  padding: 3px 6px;
  margin: 5px;
  font-size: 1rem;
  border-radius: 10px;
}

.widget-mpris {
  color: @on_primary_container;
  background: alpha(@primary_container, .8);
  padding: 3px 6px;
  margin: 5px;
  border-radius: 10px;
}

